#Mensaplan für heute
def get_mensa_response():
    session_attributes = {}
    card_title = "MensaplanResponse"

    url = "https://www.studentenwerk-potsdam.de/essen/unsere-mensen-cafeterien/detailinfos/?tx_typoscriptrendering%5Bcontext%5D=%7B%22record%22%3A%22pages_66%22%2C%22path%22%3A%22tt_content.list.20.ddfmensa_ddfmensajson%22%7D&tx_ddfmensa_ddfmensajson%5Bmensa%5D=38&cHash=0843fa029901bdfc0e76d8f31eee7f56"

    #speech_output = "Ich gebe hier den Mensaplan aus."
    parser = MensaParser(url)
    angebot = parser.get_offer_for_date(datetime.now())
    s = ""
    for x,y in angebot.items():
        s+= x + " , " + y + ".\n"

    #ersetze codes mit umlauten
    s = s.replace(r"\u00e4", r"ä")
    s = s.replace(r"\u00c4", r"Ä")
    s = s.replace(r"\u00f6", r"ö")
    s = s.replace(r"\u00d6", r"Ö")
    s = s.replace(r"\u00fc", r"ü")
    s = s.replace(r"\u00dc", r"Ü")
    s = s.replace(r"\u00df", r"ß")
    s = s.replace(r"\n", r"")

    #TODO: try utf-8
    #s.encode('utf-8')

    speech_output = s
    reprompt_text = "Ich wiederhole: Ich gebe hier den Mensaplan aus."
    should_end_session = False
    return build_response(session_attributes, build_speechlet_response(
        card_title, speech_output, reprompt_text, should_end_session))

#Mensaplan für morgigen Tag
def get_mensa_response_tomorrow():
    session_attributes = {}
    card_title = "MensaplanResponseTomorrow"
    url = "https://www.studentenwerk-potsdam.de/essen/unsere-mensen-cafeterien/detailinfos/?tx_typoscriptrendering%5Bcontext%5D=%7B%22record%22%3A%22pages_66%22%2C%22path%22%3A%22tt_content.list.20.ddfmensa_ddfmensajson%22%7D&tx_ddfmensa_ddfmensajson%5Bmensa%5D=38&cHash=0843fa029901bdfc0e76d8f31eee7f56"
    parser = MensaParser(url)
    angebot = parser.get_offer_for_date(datetime.now() + timedelta(days=1))
    s = ""
    for x, y in angebot.items():
        s += x + " , " + y + ".\n"

    # ersetze codes mit umlauten
    s = s.replace(r"\u00e4", r"ä")
    s = s.replace(r"\u00fc", r"ü")

    speech_output = s
    reprompt_text = "Ich wiederhole: Ich gebe hier den Mensaplan aus."
    should_end_session = False
    return build_response(session_attributes, build_speechlet_response(
        card_title, speech_output, reprompt_text, should_end_session))

#Willkommensnachricht
def get_welcome_response():
    session_attributes = {}
    card_title = "Welcome"
    speech_output = "Mensaparser gestartet"
    # If the user either does not reply to the welcome message or says something
    # that is not understood, they will be prompted again with this text.
